import React, {useState, useEffect} from 'react';
import { BarChart, Bar, XAxis, YAxis, Tooltip, CartesianGrid, ResponsiveContainer } from 'recharts';


// const Dashboard = () => {
//     return (
//         <div> hi</div>
//     )
// }

const Dashboard = () => {
  const [mixpanelData, setMixpanelData] = useState(null);
  const [error, setError] = useState(null);

  // Fetch Mixpanel data when the component mounts
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:3000/api/dashboard');
        const data = await response.json();
        if (response.ok) {
          setMixpanelData(data);
        } else {
          setError('Error fetching Mixpanel data');
        }
      } catch (err) {
        setError('Error fetching data');
        console.error(err);
      }
    };

    fetchData();
  }, []);
  const seriesData = mixpanelData.series;
  const data = [
    { name: "Tap Drop Marker", value: seriesData["A. Tap Drop Marker [Total Events]"].all },
    { name: "Finish Collecting Drop", value: seriesData["B. Tap Finish Collecting Drop [Total Events]"].all }
  ];
  return (
    <div>
      <h1>Dashboard</h1>
      {error && <p>{error}</p>}
      {mixpanelData ? (
        <div>
          <h2>Mixpanel Data:</h2>
          <pre>{JSON.stringify(mixpanelData, null, 2)}</pre>
        </div>
      ) : (
        <p>Loading Mixpanel data...</p>
      )}
    </div>
  );
};

export default Dashboard;
